const sleep = (n) => new Promise((resolve) => setTimeout(resolve, n));

// { id, title, body}

const posts = [
  {
    id: 1,
    title: "learn about redux middleware",
    body: "It is to understand aboutt redux-middleware",
  },
  {
    id: 2,
    title: "use redux-thunk",
    body: "비동기 해보자",
  },
  {
    id: 3,
    title: "use redux-saga",
    body: "추후 배울거임",
  },
];

export const getPosts = async () => {
  await sleep(500);
  return posts;
};

export const getPostById = async (id) => {
  await sleep(500);
  return posts.find((post) => post.id === id);
};

// sleep(1000).then(() => console.log("hello world!"));
