import axios from "axios";

// { id, title, body}

export const getPosts = async () => {
  const res = await axios.post("https://hackrss.kr/api/today/", {
    params: {
      page: 1,
    },
  });
  return res.data;
};

export const getPostById = async (id) => {
  const res = await axios.get(`http://localhost:4000/posts/${id}`);
  return res.data;
};

// sleep(1000).then(() => console.log("hello world!"));
