import React, { useState } from 'react'

function InputSample() {
    const [text, setText] = useState('');



    const onChange  = (e) => {
        console.log(e.target.value)
    }

    const onReset = () => {
        setText('');
    }


    return (
        <div>
            <input onChange={onChange} value={text} />
            <button onClick={onReset}>초기화</button>
            <div>
                <b>값 : </b>
                asdfasdf
            </div>
        </div>
    )
}

export default InputSample;
