  constructor(props) {
    super(props);
    this.state = {
      //class 에서는 state는 무조건 객체

      counter:0,
      updateMe: {
        toggleMe: false,
        dontChangeMe: 1
      }
    };
    // this.handleIncrease = this.handleIncrease.bind(this);
    // this.handleDecrease = this.handleDecrease.bind(this);
  }

  handleIncrease = () => {
    // this.setState({
