  render(){
    return (
      <div>
        <h1>{this.state.counter}</h1>
        <button onClick={this.handleIncrease}>+1</button>
        <button onClick={this.handleDecrease}>-1</button>
        <p>fixed: {this.state.fixed}</p>
      </div>
    );
  }
}



