import React, { useEffect } from 'react';

function User({ user, onRemove, onToggle }){
    const {username, email, id, active} = user;
    // useEffect(() => {
    //     console.log('componet is show')
    //     //props -> state
    //     // rest api
    //     // D3 Video.js 
    //     // setInterval, setTimeOut
    //     return () => {
    //         //setInterval, seTimeout -> clearInteval, clearTimeOut
    //         // lib instance delete
    //         console.log('component is hide')
    //     }
    // }, [])
    useEffect(()  => {
        console.log(user)
        return () => {
            console.log(user)
        }
    }, [user]);

    return (
        <div>
            <b onClick={() => onToggle(id)} style={{
                color: active ? 'green' : 'black',
                cursor: 'pointer'
            }}>{username}</b>&nbsp;<span>({email})</span>
            <button onClick={() => onRemove(id)}>삭제</button>
        </div>
    )
}

function UserList({ users, onRemove, onToggle }){


    return (
        <div>
            {
                users.map(
                    user => (<User user={user} key={user.id}
                        onRemove={onRemove} onToggle={onToggle}/>)
                )
            }
        </div>
    )
}



export default UserList;
