import React, {useRef} from 'react';
// import Hello from "./Hello";
// import Wrapper from './Wrapper';
import UserList from './UserList';




function App() {

  const users = [
    {
      id: 1,
      username: "j0dev",
      email: "asdf@asdf.com"
    },
    {
      id: 2,
      username: "intadd",
      email: "qwer@asdf.com"
    },
    {
      id: 3,
      username: "test",
      email: "zxcv@asdf.com"
    }
  ];


  const nextId = useRef(4)
  //특정 돔을 선택하고 싶을때 쓸 수 도 있지만, 어떠한 변수의 값을 계속 기억하고 싶을때 사용 -> 컴포넌트가 리렌더링 되도 유지됨
  //또한 이값이 변경되더라도 컴포넌트가 리렌더링 되지 않음

  const onCreate = () => {

    console.log(nextId.current)
    nextId.current += 1;
  }

  return (
    <UserList users = {users}/>
  );
}

export default App;
