import React from "react";
import styled, { css } from "styled-components";
import { darken, lighten } from "polished";

const colorStyles = css`
  ${({ theme, color }) => {
    const selectedColor = theme.palette[color];
    return css`
      background: ${selectedColor};
      &:hover {
        background: ${lighten(0.1, selectedColor)};
      }
      &:active {
        background: ${darken(0.1, selectedColor)};
      }
    `;
  }}
`;

const StyledButton = styled.button`
  /* 공통 스타일 */
  display: inline-flex;
  outline: none;
  border: none;
  border-radius: 4px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  padding-left: 1rem;
  padding-right: 1rem;

  ${colorStyles}
  ${sizeStyles}

  & + & {
    margin-left: 1rem;
  }
`;

const sizeStyles = css`
    ${props =>
      props.size === "large" &&
      css`
        heigh: 3rem;
        font-size: 1.25rem;
      `}

  ${props =>
    props.size === "medium" &&
    css`
      heigh: 2.25rem;
      font-size: 1rem;
    `}

    ${props =>
      props.size === "small" &&
      css`
        heigh: 1.75rem;
        font-size: 0.875rem;
      `}
`;

function Button({ children, size, color, ...rest }) {
  return (
    <StyledButton color={color} {...rest} size={size}>
      {children}
    </StyledButton>
  );
}

Button.defaultProps = {
  color: "blue",
  size: "medium"
};

export default Button;
